#define DMINUS_TABLE_SHIFT (int)(FRAC_BITS-(int)log2(1/R_DMINUS))

typedef struct  lns {
    int log: MAG_BITS; // log(|x|) in fixed point
    bool sign; // sign of x (sign=1 if x>=0 , 0 if x<0)
    bool zero; // zero flag (zero=1 if x=0, 0 otherwise)
} lns;

